#!/bin/bash

readonly SHARED_SECRET="459efd354e5246cc8d0f85a6b2546c3b"
readonly RECEIPT_DATA=""
readonly HOST="sandbox.itunes.apple.com"

curl -v -H "Content-Type: application/json;charset=UTF-8" \
     -H "Accept-Language: en-US" \
     -H "Accept-Charset: UTF-8" \
     -H "Accept: application/json" \
     -X POST \
     -d "{\"password\": \""${SHARED_SECRET}"\", \"receipt-data\": \""${RECEIPT_DATA}\""}" \
     https://${HOST}/verifyReceipt \
     -o verify-receipt-response.json
